'use strict';const progress=document.getElementById("progress"),previous=document.getElementById("previous"),next=document.getElementById("next"),circles=document.querySelectorAll(".circle");let currentActive=1;next.addEventListener("click",()=>{currentActive++;currentActive>circles.length&&(currentActive=circles.length);update()});previous.addEventListener("click",()=>{currentActive--;1>currentActive&&(currentActive=1);update()});
function update(){circles.forEach((a,b)=>{b<currentActive?a.classList.add("active"):a.classList.remove("active")});const c=document.querySelectorAll(".active");progress.style.width=`${(c.length-1)/(circles.length-1)*100}%`;1===currentActive?previous.disabled=!0:currentActive===circles.length?next.disabled=!0:(previous.disabled=!1,next.disabled=!1)};